import sys

def main():
    n = int(sys.stdin.readline())
    s = sys.stdin.readline()

    # Compute the number of colors
    color = 1
    for i in range(1, n):
        if s[i] != s[i-1]:
            color += 1

    # Compute the color of each character
    c = [1] * n
    for i in range(1, n):
        if s[i] != s[i-1]:
            c[i] = c[i-1] + 1

    # Print the result
    print(color)
    for i in range(n):
        print(c[i], end=' ')

if __name__ == '__main__':
    main()

