n, k = map(int, input().split())

def isValid(n, k):
    if k > n:
        return False
    if n == 1:
        return True
    if k == 1:
        return False
    if k == 2:
        if n == 2:
            return True
        return False
    if n%2 == 1:
        return False
    if n == 2*k:
        return True
    if n > 2*k:
        return False
    return True

