n = int(input())
x = [int(i) for i in input().split()]

d = {}
for i in x:
    if i in d:
        d[i] += 1
    else:
        d[i] = 1

if len(d) == 1:
    print(0)
else:
    m = float("inf")
    for i in d:
        if d[i] < m:
            m = d[i]
    print(m)

def main():
    n = int(input())
    x = list(map(int, input().split()))
    x.sort()
    ans = float('inf')
    for i in range(len(x)):
        ans = min(ans, sum(abs(x[i] - x[j]) for j in range(len(x))))
    print(ans)

if __name__ == '__main__':
    main()

def solve(n, x):
    min_x = min(x)
    max_x = max(x)
    if min_x == max_x:
        return 0
    elif min_x + 1 == max_x:
        return 1
    else:
        return 2

n = int(input())
x = list(map(int, input().split()))
print(solve(n, x))
