def spaghetti(n, arr):
	for i in range(len(arr)):
		if i == 0:
			continue
		if abs(arr[i] - arr[i-1]) >= 2:
			return "NO"
	return "YES"

def main():
	n = int(input())
	arr = [int(x) for x in input().split()]
	print(spaghetti(n, arr))

if __name__ == "__main__":
	main()
