import math

def main():
    N = int(input())
    ans = 0
    for i in range(1, int(math.sqrt(N)) + 1):
        if N % i == 0:
            ans += 1
    ans *= 2
    if math.sqrt(N) == int(math.sqrt(N)):
        ans -= 1
    print(ans)

if __name__ == '__main__':
    main()

