def main():
    n, a, b = [int(x) for x in input().split()]
    print(n // (a + b) * a + min(a, n % (a + b)))

if __name__ == '__main__':
    main()

def main():
    n, a, b = map(int, input().split())
    total = a + b
    if total <= n:
        print(a)
    else:
        print(a - (total - n))

if __name__ == "__main__":
    main()

