import sys
import math

def get_divisors(n):
    divisors = [1]
    for i in range(2, int(math.sqrt(n) + 1)):
        if n % i == 0:
            divisors.append(i)
            if i != math.sqrt(n):
                divisors.append(n / i)
    return divisors

def get_minimum_x(divisors):
    divisors = sorted(divisors)
    for i in range(len(divisors)):
        if divisors[i] != i + 2:
            return divisors[i - 1] * (i + 2)
    return divisors[-1] * (len(divisors) + 1)

