import math

def is_prime(n):
    if n == 2:
        return True
    if n % 2 == 0 or n <= 1:
        return False

    sqr = int(math.sqrt(n)) + 1

    for divisor in range(3, sqr, 2):
        if n % divisor == 0:
            return False
    return True

def is_power(n):
    if n == 0:
        return False
    while n % 2 == 0:
        n = n // 2
    return n == 1

def get_min_x(n):
    if is_prime(n):
        return 2 * n
    elif is_power(n):
        return 2 * (n + 1)
    else:
        return n + 1

