def check_if_interesting(n):
    if len(n) == 1:
        if int(n) % 4 == 0:
            return True
        else:
            return False
    elif len(n) == 2:
        if (int(n[0]) + int(n[1])) % 4 == 0:
            return True
        else:
            return False
    else:
        if (int(n[0]) + int(n[1]) + int(n[2])) % 4 == 0:
            return True
        else:
            return False

def find_interesting_number(a):
    while True:
        if check_if_interesting(str(a)):
            return a
        else:
            a += 1

print(find_interesting_number(int(input())))

