def sum_of_digits(n):
    return sum(map(int, str(n)))

def interesting_number(n):
    return sum_of_digits(n) % 4 == 0

def next_interesting_number(n):
    while not interesting_number(n):
        n += 1
    return n

print(next_interesting_number(int(input())))
