n = int(input())
a = [int(i) for i in input().split()]

a.sort()

d = {}

for i in range(n):
    d[a[i]] = d.get(a[i], 0) + 1

ans = 0

for i in range(n):
    if d[a[i]] == 0:
        continue
    d[a[i]] -= 1
    if a[i] - 1 in d and d[a[i] - 1] > 0:
        d[a[i] - 1] -= 1
    elif a[i] + 1 in d and d[a[i] + 1] > 0:
        d[a[i] + 1] -= 1
    else:
        ans += 1

print(ans)

def main():
    n = int(input())
    a = [int(x) for x in input().split()]
    d = {}
    for x in a:
        if x in d:
            d[x] += 1
        else:
            d[x] = 1
    s = set()
    for x in d:
        if x+d[x] in s:
            return False
        s.add(x)
    return len(s)

print(main())

import sys

def main():
    n = int(sys.stdin.readline().strip())
    a = list(map(int, sys.stdin.readline().strip().split()))
    a.sort()
    cnt, mx = 1, 1
    for i in range(1, n):
        if a[i] - a[i-1] <= 1:
            cnt += 1
        else:
            mx = max(mx, cnt)
            cnt = 1
    mx = max(mx, cnt)
    print(mx)

if __name__ == "__main__":
    main()

import sys

def main():
    n = int(input())
    a = map(int, sys.stdin.readline().split())
    a = sorted(a)
    cnt = 0
    for i in a:
        if i > cnt+1:
            break
        cnt += 1
    print(cnt)

if __name__ == '__main__':
    main()
