N = int(input())
h = list(map(int, input().split()))

def count_watering_operations(N, h):
    if N == 0:
        return 0
    max_height = max(h)
    min_watering_operations = max_height
    for i in range(1, max_height+1):
        watering_operations = 0
        for j in range(N):
            if h[j] < i:
                watering_operations += i - h[j]
        if watering_operations < min_watering_operations:
            min_watering_operations = watering_operations
    return min_watering_operations

