def main():
    N,K = map(int,input().split())
    dp = [0]*(K+1)
    dp[0] = 1
    for i in range(1,K+1):
        dp[i] = i
    for i in range(K,N+1):
        dp[i%K] += 1
    ans = 0
    for i in range(K):
        for j in range(K):
            for k in range(K):
                if (i+j)%K == 0 and (j+k)%K == 0 and (k+i)%K == 0:
                    ans += dp[i]*dp[j]*dp[k]
    if K%2 == 0:
        ans -= 3*dp[K//2]**3
    print(ans)

