def main():
    """
    Main function.
    """
    n = int(input())
    heights = list(map(int, input().split()))

    cnt = 1
    for i in range(1, n):
        if heights[i] <= heights[i-1]:
            cnt += 1
    print(cnt)


if __name__ == "__main__":
    main()

N = int(input())
H = list(map(int, input().split()))

count = 0
for i in range(N - 1):
    if H[i] >= H[i + 1]:
        count += 1
    else:
        count = 0
print(count + 1)

n = int(input())
h = list(map(int, input().split()))

cnt = 0
max_cnt = 0
for i in range(n):
    if i == 0:
        cnt += 1
    else:
        if h[i] >= h[i-1]:
            cnt += 1
        else:
            cnt = 1
    max_cnt = max(cnt, max_cnt)

print(max_cnt)
