import sys

def check_monotonic(n, heights):
    for i in range(n - 1):
        if heights[i] > heights[i + 1]:
            return False
    return True

def main():
    n = int(input())
    heights = list(map(int, input().split()))
    if check_monotonic(n, heights):
        print("Yes")
    else:
        print("No")

if __name__ == '__main__':
    main()

