# Complete the highestValuePalindrome function below.
def highestValuePalindrome(s, n, k):
    if n == 1:
        if k == 0:
            return s
        else:
            return 9
    else:
        if k == 0:
            return s
        else:
            s_list = list(s)
            num_changes = 0
            for i in range(n//2):
                if s_list[i] != s_list[n-i-1]:
                    num_changes += 1
            if num_changes > k:
                return -1
            else:
                for i in range(n//2):
                    if s_list[i] != s_list[n-i-1]:
                        if s_list[i] > s_list[n-i-1]:
                            s_list[n-i-1] = s_list[i]
                        else:
                            s_list[i] = s_list[n-i-1]
                        k -= 1
                    if s_list[i] != '9':
                        if k >= 2:
                            s_list[i] = '9'
                            s_list[n-i-1] = '9'
                            k -= 2
                if k >= 1 and n % 2 == 1:
                    s_list[n//2] = '9'
                return ''.join(s_list)

if __name__ == '__main__':
    fptr = open(os.environ['OUTPUT_PATH'], 'w')

    nk = input().split()

    n = int(nk[0])

    k = int(nk[1])

    s = input()

    result = highestValuePalindrome(s, n, k)

    fptr.write(result + '\n')

    fptr.close()

