import sys

def main():
    input = sys.stdin.readline
    N = int(input())
    permutation = list(map(int, input().split()))

    if N == 2:
        if permutation[0] == 1 and permutation[1] == 2:
            print("YES")
        else:
            print("NO")
        return

    sorted = [i for i in range(1, N + 1)]

    start = 0
    end = N - 1

    while start < end:
        if permutation[start] == sorted[start]:
            start += 1
        elif permutation[end] == sorted[end]:
            end -= 1
        else:
            permutation[start], permutation[end] = permutation[end], permutation[start]
            if permutation == sorted:
                print("YES")
                return
            else:
                print("NO")
                return
    print("YES")

