def max_difference(n, a):
	max_diff = 0
	for i in range(n):
		for j in range(n):
			if j != i:
				diff = abs(a[i] - a[j])
				if diff > max_diff:
					max_diff = diff
	return max_diff

if __name__ == '__main__':
	n = int(input())
	a = [int(x) for x in input().split()]
	print(max_difference(n, a))
