N, M = map(int, input().split())

# 各人が好きな料理を格納するリストを用意
like = []
for _ in range(N):
    like.append(list(map(int, input().split()))[1:])

# 全員が好きな料理を格納するリストを用意
all_like = []
for i in range(1, M+1):
    # 全員が好きな料理かどうかを判定
    for li in like:
        if i not in li:
            break
    else:
        all_like.append(i)

print(len(all_like))

def food_liked_by_all(N, M, K, A):
    foods = [0] * M
    for i in range(N):
        for j in range(K[i]):
            foods[A[i][j] - 1] += 1
    return sum(1 for food in foods if food == N)

if __name__ == "__main__":
    N, M = map(int, input().split())
    K = []
    A = []
    for _ in range(N):
        k = list(map(int, input().split()))
        K.append(k[0])
        A.append(k[1:])
    print(food_liked_by_all(N, M, K, A))
