import math

def is_multiple_of_9(n):
    # n is a multiple of 9 if and only if
    # the sum of the digits of n is a multiple of 9
    n = str(n)
    sum_digits = 0
    for i in range(len(n)):
        sum_digits += int(n[i])
    if sum_digits % 9 == 0:
        return True
    else:
        return False

def main():
    n = input()
    if is_multiple_of_9(n):
        print("Yes")
    else:
        print("No")

if __name__ == "__main__":
    main()

