def main():
    n = int(input())
    a = list(map(int, input().split()))
    b = list(map(int, input().split()))
    c = list(map(int, input().split()))
    ans = 0
    for i in range(n):
        ans += b[a[i] - 1]
        if i == 0:
            continue
        if a[i - 1] + 1 == a[i]:
            ans += c[a[i - 1] - 1]
    print(ans)

if __name__ == '__main__':
    main()

def main():
    N = int(input())
    A = list(map(int, input().split()))
    B = list(map(int, input().split()))
    C = list(map(int, input().split()))
    ans = 0
    for i in range(N):
        ans += B[A[i] - 1]
        if i > 0 and A[i] - A[i-1] == 1:
            ans += C[A[i-1] - 1]
    print(ans)

if __name__ == '__main__':
    main()
