    N = int(input())
    s = [0] * N
    for i in range(N):
        s[i] = input()
    ans = 0
    for i in range(N):
        for j in range(i+1, N):
            if isAnagram(s[i], s[j]):
                ans += 1
    print(ans)

def isAnagram(s1, s2):
    if len(s1) != len(s2):
        return False
    count = [0] * 26
    for i in range(len(s1)):
        count[ord(s1[i]) - ord('a')] += 1
        count[ord(s2[i]) - ord('a')] -= 1
    for i in range(26):
        if count[i] != 0:
            return False
    return True

