def main():
    """
    1. 入力
    2. ソートした文字列を文字列の辞書に格納
    3. 辞書をソートして、同じ文字列が含まれる場合は組み合わせを数える
    """
    n = int(input())
    dic = {}
    for _ in range(n):
        s = list(input())
        s.sort()
        tmp = "".join(s)
        if tmp in dic:
            dic[tmp] += 1
        else:
            dic[tmp] = 1
    ans = 0
    for k, v in dic.items():
        ans += v * (v - 1) // 2
    print(ans)


