start = max(A, C)
end = min(B, D)

if end - start < 0:
    print(0)
else:
    print(end - start)

import sys

def main():
    a, b, c, d = [int(n) for n in sys.stdin.readline().rstrip().split()]
    if a < c:
        if b < c:
            print(0)
        elif b < d:
            print(b - c)
        else:
            print(d - c)
    else:
        if a < d:
            if b < d:
                print(b - a)
            else:
                print(d - a)
        else:
            print(0)

if __name__ == '__main__':
    main()

