import sys

def f(n):
    return n//2 if n%2 == 0 else 3*n+1

def find_m(s):
    a = [s]
    while True:
        a.append(f(a[-1]))
        if a.count(a[-1]) >= 2:
            return len(a)

def main():
    s = int(sys.stdin.readline())
    print(find_m(s))

if __name__ == "__main__":
    main()

