def find_max_circle(n, a):
    if n == 1:
        return 1, [a[0]]
    a.sort()
    if a[-1] - a[0] <= 1:
        return n, a
    else:
        a_min = a.count(a[0])
        a_max = a.count(a[-1])
        if a_min > a_max:
            return n - a_min, [a[-1]] * a_min + [a[0]] * (n - a_min)
        elif a_max > a_min:
            return n - a_max, [a[0]] * a_max + [a[-1]] * (n - a_max)
        else:
            return n - a_min, [a[0]] * a_min + [a[-1]] * (n - a_min)

k, res = find_max_circle(n, a)
print(k)
print(*res)

