import sys

def main():
    num = int(sys.stdin.readline().strip())
    digits = []

    while num > 0:
        digits.append(num % 10)
        num //= 10

    if sum(digits) % len(digits) == 0:
        print('Yes')
    else:
        print('No')

if __name__ == '__main__':
    main()

