n = int(input())
a = list(map(int, input().split()))

def solve(n, a):
    if n == 1:
        return [0]
    if n == 2:
        if a[0] < a[1]:
            return [0, 0]
        if a[0] > a[1]:
            return [1, 1]
        if a[0] == a[1]:
            return [0, 1]

    if a[0] < a[1]:
        return [0] + solve(n-1, a[1:])
    if a[0] > a[1]:
        return [1] + solve(n-1, a[1:])
    if a[0] == a[1]:
        return [0, 1] + solve(n-2, a[2:])

res = solve(n, a)
