    if s[0] == 'A' and s[2] == 'B':
        return "Yes"
    elif s[0] == 'B' and s[2] == 'A':
        return "Yes"
    else:
        return "No"

def main():
    s = sys.stdin.readline().rstrip()
    print(check_bus(s))

