import sys

def main():
    # get input
    H, W = map(int, input().split())

    # calculate answer
    if H % 3 == 0 or W % 3 == 0:
        print(0)
    else:
        ans = min(H, W)
        for i in range(1, H):
            ans = min(ans, max(i * W, (H - i) * (W // 2), (H - i) * (W - W // 2)))
        for i in range(1, W):
            ans = min(ans, max(i * H, (W - i) * (H // 2), (W - i) * (H - H // 2)))
        print(ans)

if __name__ == '__main__':
    main()

def main():
    h, w = map(int, input().split())
    print(min(0, (h - 1) * w, h * (w - 1)))

