        if a[0] % 4 == 0:
            print('Yes')
        else:
            print('No')
    elif n == 2:
        if (a[0] * a[1]) % 4 == 0:
            print('Yes')
        else:
            print('No')
    else:
        a.sort(reverse=True)
        if (a[0] * a[1]) % 4 == 0:
            print('Yes')
        elif (a[0] * a[2]) % 4 == 0:
            print('Yes')
        elif (a[1] * a[2]) % 4 == 0:
            print('Yes')
        elif (a[0] * a[1] * a[2]) % 4 == 0:
            print('Yes')
        else:
            print('No')

