import sys

def solve(n, a):
    for i in range(n):
        if a[i] % 4 == 0:
            continue
        elif i == n - 1:
            return False
        elif a[i] % 2 == 0:
            continue
        elif a[i + 1] % 2 == 0:
            continue
        else:
            return False
    return True

def main():
    n = int(sys.stdin.readline())
    a = list(map(int, sys.stdin.readline().split()))
    if solve(n, a):
        print('Yes')
    else:
        print('No')

