import sys

N, T = map(int, sys.stdin.readline().rstrip().split())
t = list(map(int, sys.stdin.readline().rstrip().split()))

ans = 0
for i in range(N):
    if i == 0:
        ans += T
    else:
        if t[i] - t[i-1] > T:
            ans += T
        else:
            ans += t[i] - t[i-1]

print(ans)
