def min_moves(a, b):
    moves = 0
    if a == b:
        return 0
    while a < b:
        if a % 2 == 0:
            a += 1
        else:
            a += 2
        moves += 1
    while a > b:
        if a % 2 == 0:
            a -= 1
        else:
            a -= 2
        moves += 1
    return moves

