def sum_n(n):
    return n*(n+1)//2

def solve(n):
    s = str(n)
    l = len(s)
    return sum_n(l) + sum_n(9)*(l-1) + (int(s[0])-1)*l

for _ in range(int(input())):
    print(solve(int(input())))

def main():
    t = int(input())
    for _ in range(t):
        x = int(input())
        count = 0
        for i in range(1, len(str(x))+1):
            count += i*9
        count += len(str(x))*x
        print(count)

main()

import sys

def digits(n):
    if n < 1:
        return 0
    return n * (n + 1) // 2

def main():
    t = int(sys.stdin.readline())
    for _ in range(t):
        x = int(sys.stdin.readline())
        x = str(x)
        y = int(x[0])
        print(digits(y) * len(x))

if __name__ == "__main__":
    main()
