def main():
    n, x = map(int, input().split())
    a = list(map(int, input().split()))

    ans = 0
    for i in range(1, n):
        left = a[i-1]
        right = a[i]
        if left + right > x:
            ans += left + right - x
            a[i] -= left + right - x
    print(ans)

if __name__ == '__main__':
    main()
