from sys import stdin

def isEven(s):
    if len(s) % 2 != 0:
        return False
    else:
        return s[:len(s)//2] == s[len(s)//2:]

def main():
    s = str(stdin.readline().rstrip())
    for i in range(len(s), 1, -1):
        if isEven(s[:i]):
            print(i)
            return

if __name__ == '__main__':
    main()
