def main():
    t = int(input())
    for _ in range(t):
        n = int(input())
        a = list(map(int, input().split()))
        # a = [1, 2, 2]
        # a = [5, 5, 5, 5]
        # a = [1, 2, 4]
        # a = [1, 3, 4, 4]
        # a = [100]
        if n == 1:
            print("YES")
            continue
        if n == 2 and a[0] == a[1]:
            print("YES")
            continue
        if n == 2 and abs(a[0] - a[1]) > 1:
            print("NO")
            continue
        for i in range(n):
            for j in range(n):
                if abs(a[i] - a[j]) <= 1 and i != j:
                    a.remove(min(a[i], a[j]))
                    break
            if len(a) == 1:
                print("YES")
                break
        else:
            print("NO")

if __name__ == "__main__":
    main()
