import sys

def main():
    t = int(input())
    for _ in range(t):
        n, k = map(int, input().split())
        a = list(map(int, input().split()))
        x = 0
        ans = 0
        for i in range(n):
            if a[i] % k != 0:
                a[i] += x
                x += 1
                ans += 1
        print(ans)

if __name__ == '__main__':
    sys.exit(main())

