# define functions
def is_valid_postal_code(ab, s):
    """
    Return True if the postal code is valid, False otherwise.
    """
    # check length and hyphen
    if len(s) != (ab[0] + ab[1] + 1) or s[ab[0]] != "-":
        return False
    # check digits
    for i in range(ab[0] + ab[1] + 1):
        if s[i] == "-":
            continue
        if s[i] not in "0123456789":
            return False
    return True

# main
if is_valid_postal_code(ab, s):
    print("Yes")
else:
    print("No")

