import sys

def main():
    N = int(input())
    A = [int(x) for x in sys.stdin.readline().split()]
    if N == 1:
        print(A[0])
        return
    prod = A[0]
    for i in range(1, N):
        prod = prod * A[i]
        if prod > 1000000000000000000:
            print(-1)
            return
    print(prod)

if __name__ == '__main__':
    main()

import math

def main():
    N = int(input())
    A = list(map(int, input().split()))
    result = 1
    for i in range(N):
        result *= A[i]
        if result > math.pow(10, 18):
            print(-1)
            return
    print(result)

if __name__ == '__main__':
    main()
