def main():
    N = int(input())
    S = input()
    A = [0] * N
    B = [0] * N
    for i in range(N):
        if S[i] == 'E':
            A[i] = 1
        else:
            A[i] = -1
    for i in range(N-1):
        A[i+1] += A[i]
    for i in range(N):
        if S[N-i-1] == 'W':
            B[N-i-1] = 1
        else:
            B[N-i-1] = -1
    for i in range(N-1):
        B[N-i-2] += B[N-i-1]
    print(min(A[i] + B[i] for i in range(N)))

if __name__ == '__main__':
    main()

n = int(input())
s = input()

e, w = 0, 0
for i in range(n):
    if s[i] == "E":
        e += 1
    else:
        w += 1

ans = n
for i in range(n):
    if s[i] == "E":
        e -= 1
    else:
        w -= 1
    ans = min(ans, e + w)
print(ans)
