import sys
sys.setrecursionlimit(1000000)

def solve(A, B, C, X):
    if A == 0 and B == 0 and C == 0 and X == 0:
        return 1
    if A < 0 or B < 0 or C < 0 or X < 0:
        return 0
    return solve(A - 1, B, C, X - 500) + solve(A, B - 1, C, X - 100) + solve(A, B, C - 1, X - 50)

if __name__ == '__main__':
    A = int(input())
    B = int(input())
    C = int(input())
    X = int(input())
    print(solve(A, B, C, X))

