    for _ in range(t):
        n = int(input())
        w = list(map(int, input().split()))
        print(solve(n, w))

def max_num_teams(weights):
    weights.sort()
    max_teams = 0
    for i in range(len(weights)):
        for j in range(i+1, len(weights)):
            if weights[i] + weights[j] <= weights[-1]:
                max_teams += 1
            else:
                break
    return max_teams

