from collections import defaultdict
from random import shuffle
from string import ascii_lowercase
from typing import List

def generate_string(n: int, k: int) -> str:
    alphabet = list(ascii_lowercase[:k])
    shuffle(alphabet)
    if n % k == 0:
        return (n // k) * alphabet[0]
    else:
        return (n // k) * alphabet[0] + alphabet[1]

def solve(n: int, k: int) -> str:
    return generate_string(n, k)

