import sys

def main():
    t = int(input())
    for i in range(t):
        n, k = map(int, input().split())
        a = list(map(int, input().split()))
        b = list(map(int, input().split()))
        a.sort()
        b.sort(reverse=True)
        res = 0
        for i in range(n):
            if a[i] < b[i]:
                if i < k:
                    res += b[i]
                else:
                    res += a[i]
            else:
                res += a[i]
        print(res)

if __name__ == '__main__':
    main()

def maxSum(a, b, k):
    a = sorted(a)
    b = sorted(b)
    b = b[::-1]
    sum = 0
    for i in range(len(a)):
        if k > 0:
            if a[i] < b[i]:
                sum += b[i]
                k -= 1
            else:
                sum += a[i]
        else:
            sum += a[i]
    return sum

t = int(input())
for i in range(t):
    n, k = map(int, input().split())
    a = list(map(int, input().split()))
    b = list(map(int, input().split()))
    print(maxSum(a, b, k))
