def is_good(a):
    for i, v in enumerate(a):
        if i % 2 != v % 2:
            return False
    return True

def find_min_moves(a):
    if is_good(a):
        return 0

    even_positions = []
    odd_positions = []
    for i, v in enumerate(a):
        if i % 2 == 0:
            even_positions.append(v)
        else:
            odd_positions.append(v)

    even_positions.sort()
    odd_positions.sort()

    moves = 0
    for i, v in enumerate(a):
        if i % 2 == 0:
            if v != even_positions[i // 2]:
                moves += 1
        else:
            if v != odd_positions[i // 2]:
                moves += 1

    return moves

