import sys

def read_line():
    return sys.stdin.readline().rstrip()

def read_int():
    return int(read_line())

def read_ints():
    return list(map(int, read_line().split()))

def solve(a):
    n = len(a)
    even = 0
    odd = 0
    for i, x in enumerate(a):
        if x % 2 == i % 2:
            continue
        if i % 2 == 0:
            even += 1
        else:
            odd += 1
    if odd == even:
        return odd
    else:
        return -1

def main():
    t = read_int()
    for _ in range(t):
        n = read_int()
        a = read_ints()
        print(solve(a))

