def main():
	s = input()
	l = len(s)
	i = 0
	while i < l:
		if s[i] == 'B':
			s = s[:i-1] + s[i+1:]
			l = len(s)
			i -= 1
		else:
			i += 1
	print(s)

if __name__ == '__main__':
	main()

