import sys

def lucas_num(n):
    if n == 0:
        return 2
    elif n == 1:
        return 1
    else:
        return lucas_num(n-1)+lucas_num(n-2)

def main():
    n = int(sys.stdin.readline().rstrip())
    print(lucas_num(n))

if __name__ == '__main__':
    main()

